mapper.select:
select count(*)
from userinfo
where id = #{id} and email = #{email} and age = #{age} and name = #{name}

mapper.update:
update userinfo
pw = #{pw}, email = #{email}, name = #{name}, age = #{age}, grade = #{grade}
where id = #{id}

mapper.delete:
update userinfo
set delete_yn = 1
where id = #{id};
