mapper.insert:
insert into threadboard(
title, id, content
) values (
#{title}, #{id}, #{content}
)

mapper.update:
update threadboard
set title = #{title},
id = #{id},
content = #{content}
where no = #{no}

mapper.insert:
select LAST_INSERT_ID();
insert into imgboard (board_no, original_name, save_path, size) values
(#{board_id}, #{item.original_name}, #{item.save_path}, #{item.size})

mapper.select:
select save_path
from imgboard
where original_name = #{original_name}
